import react, { useState } from "react";

setInterval(App , 10);
function App(){
    let newTime = new Date().toLocaleTimeString();
    let [currTime , updatedTime] = useState(newTime);
        
        // updatedTime(newTime);
        setInterval(()=>{
            newTime = new Date().toLocaleTimeString();
            updatedTime(newTime);
        } , 100);
    return (
    <>
    <div>
        <h1>{currTime}</h1>
        <button > Time update </button>
    </div>
    </>)
}
export default App;
