import react, { useState } from "react";
import App from "./new";
import Main from "./Main";

    const filtercat = [...new Set(App.map((currelem)=>{
            return currelem.category;
        }))]
console.log(filtercat)

function Nowdata(){
    const [currData , SetcurrData ] = useState(App);
    const [currbtn , Setcurrbtn ] = useState(App);

    const filtermobile = (catagery)=>{
        const updatedList = App.filter((currelem)=>{
            return currelem.category === catagery;
        })
        SetcurrData(updatedList);
    }

    const prizefilter = (prize)=>{
        const upList = App.filter((curr)=>{
            return parseInt(curr.prize) <= prize;
        })
        SetcurrData(upList);
    }

    return (
    <>
        <div className="btns">
                <button className="All sambtn" onClick = {()=>SetcurrData(App)}>All</button>
                <button className="samsung sambtn" onClick = {()=>filtermobile("samsung")}> samsung</button>
                <button className="mi sambtn" onClick = {()=>filtermobile("mi")}>MI</button>
                <button className="filt sambtn" onClick = {()=>prizefilter(10000)}>above 10,000</button>

        </div>
    <Main currData={currData}/>
    </>
    )}

export default Nowdata;
